<template>
  <div>
    <div class="MenuHeader">
      <Menubar :model="items" :style="{ '--tabview-color': storeColor }">
        <template #start>
          <svg
            width="35"
            height="32"
            viewBox="0 0 35 32"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
            xmlns:xlink="http://www.w3.org/1999/xlink"
          >
            <rect
              x="0.420898"
              width="33.8824"
              height="32"
              fill="url(#pattern0)"
            />
            <defs>
              <pattern
                id="pattern0"
                patternContentUnits="objectBoundingBox"
                width="1"
                height="1"
              >
                <use
                  xlink:href="#image0_190_212"
                  transform="matrix(0.00377358 0 0 0.00399556 0 -0.00144281)"
                />
              </pattern>
              <image
                id="image0_190_212"
                width="265"
                height="251"
                xlink:href="data:image/png;base64,"
              />
            </defs>
          </svg>
          <span class="logoHeading">艾立斯診所</span>
        </template>
        <template #item="{ item }">
          <a
            :class="{ active: '#' + $route.path === item.url }"
            :href="item.url"
            class="p-menuitem-link"
            >{{ item.label }}</a
          >
        </template>
      </Menubar>
    </div>
    <div class="chooseTheme">
      <div
        class="themeButton"
        label=""
        :style="{ background: storeColor }"
        @click="openPosition('right')"
      >
        <!-- 選擇主題色 -->
        <span>色</span>
        <span>題</span>
        <span>主</span>
        <span>擇</span>
        <span>選</span>
        <!--                <img src="http://techext-001-site79.itempurl.com/wp-admin/images/icons/leftarrow.png" class="leftarrow" />-->
        <img
          src="https://iscomfort.smartclinics.tw/wp-content/uploads/icons/leftarrow.png"
          class="leftarrow"
        />
      </div>
    </div>
    <Dialog
      v-model:visible="visible"
      class="choosThemeModel"
      :style="{ width: '25vw' }"
      :position="position"
      :modal="true"
      :draggable="false"
    >
      <div class="chooseColorID">
        <div
          class="colorDiv"
          style="background-color: #2f7e20"
          @click="selectThemeColor('#2F7E20')"
        >
          <i v-if="this.storeColor == '#2F7E20'" class="pi pi-check"></i>
        </div>
        <div
          class="colorDiv"
          style="background-color: #00b4d8"
          @click="selectThemeColor('#00B4D8')"
        >
          <i v-if="this.storeColor == '#00B4D8'" class="pi pi-check"></i>
        </div>
        <div
          class="colorDiv"
          style="background-color: #3b76a7"
          @click="selectThemeColor('#3B76A7')"
        >
          <i v-if="this.storeColor == '#3B76A7'" class="pi pi-check"></i>
        </div>
        <div
          class="colorDiv"
          style="background-color: #9e9189"
          @click="selectThemeColor('#9E9189')"
        >
          <i v-if="this.storeColor == '#9E9189'" class="pi pi-check"></i>
        </div>
        <div
          class="colorDiv"
          style="background-color: #07beb8"
          @click="selectThemeColor('#07BEB8')"
        >
          <i v-if="this.storeColor == '#07BEB8'" class="pi pi-check"></i>
        </div>
      </div>
    </Dialog>
  </div>
</template>

<script lang="ts">
import { defineComponent } from "vue";
import { mapActions, mapState } from "vuex";

export default defineComponent({
  methods: {
    ...mapActions(["setSelectedColor"]),
    // ...mapState(["selectedColor"]), // FOR THIS WE WILL USE LATER FOR AVOIDE TWO TIME DEFINE DEFAULT COLOR CODE #07BEB8
    // POPUP PROCESS
    openPosition(position: string): void {
      this.position = position;
      this.visible = true;
    },
    // SELECT COLOR
    selectThemeColor(colorCode: string): void {
      localStorage.setItem("ThemeColor", colorCode);
      localStorage.setItem("ThemeColorNoHash", colorCode.replace(/^#/, ""));
      this.storeColor = colorCode;
      this.setSelectedColor(colorCode);
    },
    // ON LOAD CHECK AND SET COLOR
    setColor(): void {
      if (localStorage.getItem("ThemeColor") === null) {
        this.storeColor = this.defaultColor;
      } else {
        this.storeColor = localStorage.getItem("ThemeColor") as string;
      }
    },
  },
  created() {
    // SET DEFAULT COLOR
    if (localStorage.getItem("ThemeColor") === null) {
      localStorage.setItem(
        "ThemeColorNoHash",
        this.defaultColor.replace(/^#/, "")
      );
      localStorage.setItem("ThemeColor", this.defaultColor);
    }
    this.setColor();
  },
  data() {
    return {
      defaultColor: "#07BEB8",
      items: [
        {
          label: "首頁",
          url: "#/home",
        },
        {
          label: "關於診所",
          url: "#/about",
        },
        {
          label: "網路掛號",
          url: "#/registration",
        },
        {
          label: "診療項目",
          url: "#/treatment",
        },
        {
          label: "門診時間",
          url: "#/clinic_hours",
        },
        {
          label: "衛教專欄",
          url: "#/health_education",
        },
        {
          label: "常見問題",
          url: "#/common_problem",
        },
      ],
      storeColor: "",
      position: "center",
      visible: false,
    };
  },
});
</script>
